#UHSearch #UHSearchWeb{
    -webkit-animation: blueanimation 4s; /* Chrome, Safari, Opera */
    animation: blueanimation 4s;
}

#UHSearch #UHSearchBox{
    -webkit-animation: inputanimation 4s; /* Chrome, Safari, Opera */
    animation: inputanimation 4s;
}

/* Chrome, Safari, Opera */
@-webkit-keyframes blueanimation {
    0%   {background: #5700ed; box-shadow: 0px 2px black;}
    34%  {background: #3372e0; box-shadow: 0px 2px #21487F;}
    67%  {background: #5700ed; box-shadow: 0px 2px black;}
    100% {background: #3372e0; box-shadow: 0px 2px #21487F;}
}

/* Standard syntax */
@keyframes blueanimation {
    0%   {background: #5700ed; box-shadow: 0px 2px black;}
    34%  {background: #3372e0; box-shadow: 0px 2px #21487F;}
    67%  {background: #5700ed; box-shadow: 0px 2px black;}
    100% {background: #3372e0; box-shadow: 0px 2px #21487F;}
}

/* Chrome, Safari, Opera */
@-webkit-keyframes inputanimation {
    0%   {border-color: #5700ed;}
    34%  {border-color: #3372e0;}
    67%  {border-color: #5700ed;}
    100% {border-color: #3372e0;}
}

/* Standard syntax */
@keyframes inputanimation {
    0%   {border-color: #1f4781;}
    34%  {border-color: #3372e0;}
    67%  {border-color: #1f4781;}
    100% {border-color: #3372e0;}
